    private BuscarClienteMySQL scliente = new BuscarClienteMySQL();
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String dni = this.jTextField1.getText();
        String msg = scliente.buscarCliente(dni);
        this.jTextArea1.setText(msg);
        String msg2 = scliente.buscarCliente2(dni);
        this.jTextArea2.setText(msg2);
        jButton2.requestFocus();
    }//GEN-LAST:event_jButton1ActionPerformed

