    ModificarEstadoMySQL mestado = new ModificarEstadoMySQL();
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String id = this.jTextField1.getText();
        String nombre = this.jTextField2.getText();
        String msg = mestado.modificarEstado(id,nombre.toUpperCase());
        this.jTextArea1.setText(msg);

        jButton2.requestFocus();
    }//GEN-LAST:event_jButton1ActionPerformed

